JavaScript Example

javascript title:"JavaScript Class Example"
// ES6 Class with various syntax elements
class ThemeManager {
    constructor(options = {}) {
        this.themes = options.themes || [];
        this.currentTheme = options.defaultTheme || 'light';
        this.callbacks = new Map();
        this.initialized = false;
    }

    async initialize() {
        try {
            // Load saved theme from localStorage
            const saved = localStorage.getItem('theme');
            if (saved && this.themes.includes(saved)) {
                this.currentTheme = saved;
            }

            // Apply the theme
            await this.applyTheme(this.currentTheme);
            this.initialized = true;

            return { success: true, theme: this.currentTheme };
        } catch (error) {
            console.error('Failed to initialize theme:', error);
            return { success: false, error: error.message };
        }
    }

    applyTheme(themeName) {
        // Validate theme exists
        if (!this.themes.includes(themeName)) {
            throw new Error(Theme "${themeName}" not found);
        }

        // Apply theme to DOM
        document.documentElement.setAttribute('data-theme', themeName);

        // Save preference
        localStorage.setItem('theme', themeName);

        // Trigger callbacks
        this.callbacks.forEach(callback => callback(themeName));
    }

    // Arrow function property
    onChange = (callback) => {
        const id = Date.now();
        this.callbacks.set(id, callback);
        return () => this.callbacks.delete(id);
    }
}

// Usage
const manager = new ThemeManager({
    themes: ['light', 'dark', 'monokai'],
    defaultTheme: 'dark'
});

manager.initialize().then(result => {
    console.log('Theme initialized:', result);
});


Python Example

python title:"Python Data Processing"
import pandas as pd
import numpy as np
from datetime import datetime, timedelta
from typing import List, Dict, Optional, Union
import asyncio

class DataProcessor:
    """Process and analyze time series data with various transformations."""

    def init(self, datapath: str, cachesize: int = 1000):
        self.datapath = datapath
        self.cachesize = cachesize
        self.cache: Dict[str, pd.DataFrame] = {}
        self.metadata = {
            'created': datetime.now(),
            'version': '1.0.0',
            'author': 'System'
        }

    @property
    def iscached(self) -> bool:
        """Check if data is currently cached."""
        return len(self.cache) > 0

    async def loaddataasync(self, 
                            filepath: str, 
                            columns: Optional[List[str]] = None) -> pd.DataFrame:
        """Asynchronously load data from CSV file."""
        try:
            # Simulate async operation
            await asyncio.sleep(0.1)

            df = pd.readcsv(filepath, usecols=columns)

            # Cache the result
            if len(self.cache) < self.cachesize:
                self.cache[filepath] = df

            return df

        except FileNotFoundError as e:
            print(f"Error: File {filepath} not found")
            raise e
        except Exception as e:
            print(f"Unexpected error: {str(e)}")
            raise

    def processtimeseries(self, 
                          df: pd.DataFrame, 
                          windowsize: int = 7,
                          aggregation: str = 'mean') -> pd.DataFrame:
        """Apply rolling window calculations to time series data."""

        # Validate inputs
        if windowsize <= 0:
            raise ValueError("Window size must be positive")

        validaggregations = ['mean', 'sum', 'min', 'max', 'std']
        if aggregation not in validaggregations:
            raise ValueError(f"Aggregation must be one of {validaggregations}")

        # Apply rolling calculation
        numericcols = df.selectdtypes(include=[np.number]).columns

        result = df.copy()
        for col in numericcols:
            if aggregation == 'mean':
                result[f'{col}rolling{windowsize}d'] = df[col].rolling(windowsize).mean()
            elif aggregation == 'sum':
                result[f'{col}rolling{windowsize}d'] = df[col].rolling(windowsize).sum()
            # ... etc

        return result

    @staticmethod
    def generatesampledata(nrows: int = 1000) -> pd.DataFrame:
        """Generate sample time series data for testing."""
        dates = pd.daterange(
            start='2024-01-01', 
            periods=nrows, 
            freq='D'
        )

        data = {
            'date': dates,
            'value': np.random.randn(nrows).cumsum() + 100,
            'volume': np.random.randint(1000, 10000, nrows),
            'category': np.random.choice(['A', 'B', 'C'], nrows)
        }

        return pd.DataFrame(data)

Example usage
if name == "main":
    processor = DataProcessor("./data")

    # Generate and process sample data
    df = DataProcessor.generatesampledata(365)
    processed = processor.processtimeseries(df, windowsize=30)

    print(f"Data shape: {processed.shape}")
    print(f"Columns: {list(processed.columns)}")


