THEMESDIR="./themes"
BUILDDIR="./dist"
THEMEFILES=(
    "light.css"
    "dark.css"
    "vscode-dark-plus.css"
    "monokai.css"
    "dracula.css"
    "one-dark-pro.css"
    "solarized-light.css"
    "solarized-dark.css"
    "github-light.css"
    "github-dark.css"
    "nord.css"
    "gruvbox-dark.css"
    "gruvbox-light.css"
    "tokyo-night.css"
    "palenight.css"
)

Function to print colored output
log() {
    local level=$1
    shift
    case "$level" in
        "error")
            echo -e "${RED}[ERROR]${NC} $" >&2
            ;;
        "success")
            echo -e "${GREEN}[SUCCESS]${NC} $"
            ;;
        "warning")
            echo -e "${YELLOW}[WARNING]${NC} $"
            ;;
        )
            echo "[INFO] $"
            ;;
    esac
}

Validate theme files exist
validatethemes() {
    log "info" "Validating theme files..."

    local missingthemes=()

    for theme in "${THEMEFILES[@]}"; do
        if [[ ! -f "$THEMESDIR/$theme" ]]; then
            missingthemes+=("$theme")
        fi
    done

    if [[ ${#missingthemes[@]} -gt 0 ]]; then
        log "error" "Missing theme files:"
        printf '%s\n' "${missingthemes[@]}"
        return 1
    fi

    log "success" "All ${#THEMEFILES[@]} theme files found"
    return 0
}

Minify CSS files
minifythemes() {
    log "info" "Minifying theme files..."

    # Create build directory if it doesn't exist
    mkdir -p "$BUILDDIR/themes"

    for theme in "${THEMEFILES[@]}"; do
        local input="$THEMESDIR/$theme"
        local output="$BUILDDIR/themes/${theme%.css}.min.css"

        # Simple CSS minification (remove comments and extra whitespace)
        if command -v csso &> /dev/null; then
            csso "$input" -o "$output"
        else
            # Fallback: basic minification with sed
            sed -e 's/\/\[^]\\///g' \
                -e 's/\s\+/ /g' \
                -e 's/:\s/:/g' \
                -e 's/;\s/;/g' \
                -e 's/{\s/{/g' \
                -e 's/}\s/}/g' \
                -e 's/\n//g' \
                "$input" > "$output"
        fi

        log "success" "Minified: ${theme%.css}.min.css"
    done
}

Generate theme metadata
generatemetadata() {
    log "info" "Generating theme metadata..."

    cat > "$BUILDDIR/themes/themes.json" << EOF
{
    "themes": [
        $(for i in "${!THEMEFILES[@]}"; do
            theme="${THEMEFILES[$i]}"
            themeid="${theme%.css}"

            # Add comma except for last item
            if [[ $i -lt $((${#THEMEFILES[@]} - 1)) ]]; then
                echo "        \"$themeid\","
            else
                echo "        \"$themeid\""
            fi
        done)
    ],
    "count": ${#THEMEFILES[@]},
    "generated": "$(date -u +"%Y-%m-%dT%H:%M:%SZ")"
}
EOF

    log "success" "Generated theme metadata"
}

Main execution
main() {
    log "info" "Starting theme build process..."

    # Validate themes
    if ! validatethemes; then
        log "error" "Theme validation failed"
        exit 1
    fi

    # Minify themes
    minifythemes

    # Generate metadata
    generatemetadata

    log "success" "Theme build completed successfully!"
    log "info" "Output directory: $BUILDDIR"
}

Run main function
