sql title:"Theme Usage Analytics"
-- Create tables for theme usage tracking
CREATE TABLE IF NOT EXISTS themeusage (
    id SERIAL PRIMARY KEY,
    userid INTEGER NOT NULL,
    themeid VARCHAR(50) NOT NULL,
    appliedat TIMESTAMP DEFAULT CURRENTTIMESTAMP,
    sessionduration INTEGER, -- in seconds
    devicetype VARCHAR(20),
    browser VARCHAR(50),
    systemtheme VARCHAR(10) -- 'light' or 'dark'
);

-- Index for performance
CREATE INDEX idxthemeusageusertheme ON themeusage(userid, themeid);
CREATE INDEX idxthemeusageappliedat ON themeusage(appliedat);

-- View for theme popularity
CREATE OR REPLACE VIEW themepopularity AS
SELECT 
    themeid,
    COUNT(DISTINCT userid) as uniqueusers,
    COUNT() as totalapplications,
    AVG(sessionduration) as avgsessionduration,
    MAX(appliedat) as lastused
FROM themeusage
WHERE appliedat >= CURRENTDATE - INTERVAL '30 days'
GROUP BY themeid
ORDER BY uniqueusers DESC;

-- Function to get user's theme history
CREATE OR REPLACE FUNCTION getuserthemehistory(puserid INTEGER)
RETURNS TABLE (
    themeid VARCHAR(50),
    appliedat TIMESTAMP,
    durationhours NUMERIC,
    devicetype VARCHAR(20)
) AS $$
BEGIN
    RETURN QUERY
    WITH themesessions AS (
        SELECT 
            tu.themeid,
            tu.appliedat,
            tu.devicetype,
            LEAD(tu.appliedat) OVER (
                PARTITION BY tu.userid 
                ORDER BY tu.appliedat
            ) as nextappliedat
        FROM themeusage tu
        WHERE tu.userid = puserid
    )
    SELECT 
        ts.themeid,
        ts.appliedat,
        ROUND(
            EXTRACT(EPOCH FROM (
                COALESCE(ts.nextappliedat, CURRENTTIMESTAMP) - ts.appliedat
            )) / 3600, 
            2
        ) as durationhours,
        ts.devicetype
    FROM themesessions ts
    ORDER BY ts.appliedat DESC
    LIMIT 50;
END;
$$ LANGUAGE plpgsql;

-- Query to find most popular themes by time of day
WITH hourlyusage AS (
    SELECT 
        themeid,
        EXTRACT(HOUR FROM appliedat) as hourofday,
        COUNT() as usagecount
    FROM themeusage
    WHERE appliedat >= CURRENTDATE - INTERVAL '7 days'
    GROUP BY themeid, hourofday
),
rankedthemes AS (
    SELECT 
        hourofday,
        themeid,
        usagecount,
        ROWNUMBER() OVER (
            PARTITION BY hourofday 
            ORDER BY usagecount DESC
        ) as rank
    FROM hourlyusage
)
SELECT 
    hourofday,
    themeid as mostpopulartheme,
    usagecount
FROM rankedthemes
WHERE rank = 1
ORDER BY hourofday;


